INSERT INTO `product` VALUES (213, NULL, 'Đèn sứ thấu quang GLAM Trụ trắng khắc nổi chim', 'DSTQ-0010', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 4990000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Đèn sứ thấu quang GLAM trụ trắng khắc nổi chim được sản xuất trực tiếp tại gốm sứ Bát Tràng. Sản phẩm được làm bằng chất liệu sứ thấu quang đặc biệt, đảm bảo ánh sáng có thể lọt qua. </p>\r\n<p>Đèn sứ thấu quang GLAM trụ trắng khắc nổi chim có thiết kế hình trụ, hoạ tiết chim được khắc nổi hoàn toàn bằng thủ công bởi đôi bàn tay khéo léo của người nghệ nhân làm gốm kết hợp với màu men trắng đồng nhất tạo nên màu sắc 3D sống động, tôn lên sự sang trọng cho không gian nhà bạn.</p>\r\n<p>Đèn sứ thấu quang GLAM trụ trắng khắc nổi chim có thể trưng bày, làm đèn trang trí, đèn ngủ, khi bật điện lên ánh sáng xuyên qua lớp sương sứ tạo cho hình ảnh có chiều sâu, sống động đa chiều, họa tiết tinh tế lung linh huyền ảo – Chân đế đã được đấu sẵn dây điện và bóng đèn, có công tắc bật tắt khi sử dụng. Chân đế rời , dễ dàng thay bóng đèn và vệ sinh đèn khi sử dụng</p>\r\n<p>Đèn sứ thấu quang GLAM trụ trắng khắc nổi chim là một tác phẩm nghệ thuật độc đáo Đây cũng là món quà vô cùng tuyêt vời để tặng bạn bè người thân nhân dịp lễ, tết, tân gia,.....</p>\r\n<p>Sản phẩm được sử dụng nguồn điện 220v với công suất bóng điện bên trong 40w rất dễ thay và sửa chữa khi hỏng bóng.</p>', 6, 'A', NULL, NULL, '2017-04-23 10:49:54', 1, NULL, NULL);
INSERT INTO `product` VALUES (214, NULL, 'Đèn sứ thấu quang GLAM Trụ đại dương', 'DSTQ-0006', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 4990000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Đèn sứ thấu quang GLAM trụ vẽ đại dương được làm từ sương đất cao cấp, nung ở nhiệt độ gần 1300 độ C đảm bảo độ sáng trong, bền đẹp của sản phẩm. </p>\r\n<p>Với tinh hoa gốm sứ lâu đời, người thợ gốm đã thổi hồn vào những chiếc đèn khắc lên từng hoạ tiết tỉ mỉ, tinh tế. Sản phẩm là một bức tranh đại dương thu nhỏ mang đến không gian thanh tịnh, nhẹ nhàng cho ngôi nhà của bạn.</p>\r\n<p>Đèn sứ thấu quang GLAM trụ vẽ đại dương có thể trưng bày, làm đèn trang trí, đèn ngủ, khi bật điện lên ánh sáng xuyên qua lớp sương sứ tạo cho hình ảnh có chiều sâu, sống động đa chiều, họa tiết tinh tế lung linh huyền ảo. Chân đế đã được đấu sẵn dây điện và bóng đèn, có công tắc bật tắt khi sử dụng. Chân đế rời , dễ dàng thay bóng đèn và vệ sinh đèn khi sử dụng. </p>\r\n<p>Sản phẩm cũng là món quà vô cùng ý nghĩa để tặng bạn bè, người thân nhân dịp đặc biệt.</p>\r\n<p> </p>\r\n<p>Đại dương là một thế giới bao la với những điều kì thú, những cảnh đẹp tuyệt sắc và vô vàn điều bí ẩn chờ chúng ta khám phá. Đại dương giống như quả cầu nước bao bọc sự sống của triệu triệu sinh vật biển. Những thảm san hô mềm mại rung động theo từng nhịp sóng, các loài thuỷ ngư rực rỡ sắc màu bơi theo dòng hải lưu, biển cả bình yên, tĩnh lặng. Đặt một chiếc sứ thấu quang GLAM trụ vẽ đại dương trong phòng khách, phòng làm việc ta sẽ như được trải nghiệm thế giới đại dương huyền bí, chiêm ngưỡng một thuỷ cung lộng lẫy, nguy nga ngay chính ngôi nhà của mình.</p>', 3, 'A', NULL, NULL, '2017-04-23 10:52:23', 1, NULL, NULL);
