INSERT INTO `product` VALUES (216, NULL, 'Đèn ngủ bình sứ vẽ ngàn hoa bọc đồng có chao', 'DSTQ-DN002', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 4400000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Đèn ngủ bình sứ vẽ ngàn hoa bọc đồng có chao được làm bằng chất liệu sứ cao cấp, nung ở nhiệt độ 1280 độ C, cốt sứ bền, đẹp và chịu được va chạm nhẹ. Đèn có kiểu dáng thanh thoát, đơn giản nhưng không kém phần sang trọng, hoạ tiết ngàn hoa khéo léo tinh tế phù hợp với nội thất trong phòng khách và phòng ngủ của bạn.Đèn được trang bị dây chiết áp rất thuận tiện khi có thể đọc sách và làm đèn ngủ.</p>\r\n<p>Đặc biệt, sản phẩm cũng là món quà vô cùng ý nghĩa để tặng bạn bè và người thân nhân dịp lễ, tết, tân gia,...</p>', 8, 'A', NULL, NULL, '2017-04-23 11:17:20', 1, NULL, NULL);
