INSERT INTO `product` VALUES (218, NULL, 'Đèn sứ thấu quang GLAM Trụ sen hoa màu cam', 'DSTQ-0011', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 4990000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p><strong>Đèn sứ thấu quang GLAM trụ sen hoa màu cam</strong> được làm từ sương đất cao cấp, nung ở nhiệt độ gần 1300 độ C đảm bảo độ sáng trong, bền đẹp của sản phẩm. </p>\r\n<p>Với tinh hoa gốm sứ lâu đời, người thợ gốm đã thổi hồn vào chiếc đèn khắc lên từng hoạ tiết tỉ mỉ, tinh tế. Sản phẩm có thiết kế hình trụ  vẽ hoạ tiết sen cá mềm mại, thanh thoát nổi bật trên nèn men màu cam tươi mới, rực rỡ . Sản phẩm là một tác phẩm nghệ thuật mang đến không gian ấm áp, vui tươi cho ngôi nhà của bạn.</p>\r\n<p><strong>Đèn sứ thấu quang GLAM trụ sen hoa màu cam</strong> có thể trưng bày, làm đèn trang trí, đèn ngủ, khi bật điện lên ánh sáng xuyên qua lớp sương sứ tạo cho hình ảnh có chiều sâu, sống động đa chiều, họa tiết tinh tế lung linh huyền ảo. Chân đế đã được đấu sẵn dây điện và bóng đèn, có công tắc bật tắt khi sử dụng. Chân đế rời , dễ dàng thay bóng đèn và vệ sinh đèn khi sử dụng. </p>\r\n<p>Sản phẩm cũng là món quà vô cùng ý nghĩa để tặng bạn bè, người thân nhân dịp đặc biệt.</p>\r\n<p> </p>\r\n<p>Từ xưa hoa sen đã đi sâu vào cuộc sống và văn hoá của dân tộc Việt. Hoa sen loài hoa duy nhất hội tụ đầy đủ trong mình ý nghĩa triết học, nhân sinh cao quý, là ý nghĩa về âm dương ngũ hành và sức vươn dậy của một ý chí sống mãnh liệt như người dân Việt Nam. Với vẻ đẹp thuần khiết, tao nhã hoa sen đã vươn lên đỉnh cao trở thành Quốc hoa của dân tộc. Tinh thần cư  trần bất nhiễm trần , biểu tượng cho giá trị của đạo đức, sự thuận khiết và thánh thiện. Sen trở thành nguồn cảm hứng sáng tác vô tận của các nhân sĩ. Cá chép biểu tượng cho sự kiên trì, bền bỉ, thiêng liêng cao quý, sự thăng tiến công danh và tài lộc. Đặt một chiếc đèn sứ vẽ sen cá trong phòng khách, phòng làm việc sẽ giúp gia chủ điều hoà sinh khí, mang lại tài lộc, sức khoẻ, thịnh vượng, tâm hồn thanh tịnh, xua đuổi những điềm dữ, điềm hung. Tác phẩm nghệ thuật giúp không gian sống của bạn trở nên quyến rũ, hấp dẫn.</p>', 8, 'A', NULL, NULL, '2017-04-23 11:20:48', 1, NULL, NULL);
