INSERT INTO `product` VALUES (222, NULL, 'Đèn sứ thấu quang GLAM Bát giác hoa văn nổi', 'DSTQ-0002', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 2900000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Đèn sứ thấu quang GLAM bát giác hoa văn nổi màu sắc sinh động, được nghệ nhân làng nghề 100% VẼ TAY, họa tiết sắc nét, hình ảnh độc đáo, có hồn. Đây là một tác phẩm nghệ thuật độc đáo, sương đất cao cấp, nung ở nhiệt độ gần 1300 độ C đảm bảo độ sáng trong, bền đẹp của sản phẩm. </p>\r\n<p>Đèn sứ thấu quang GLAM bát giác hoa văn nổi có thể trưng bày, làm đèn trang trí, đèn ngủ, khi bật điện lên ánh sáng xuyên qua lớp sương sứ tạo cho hình ảnh có chiều sâu, sống động đa chiều, họa tiết tinh tế lung linh huyền ảo. Chân đế đã được đấu sẵn dây điện và bóng đèn, có công tắc bật tắt khi sử dụng. Chân đế rời, dễ dàng thay bóng đèn và vệ sinh đèn khi sử dụng. </p>\r\n<p>Sản phẩm cũng là món quà vô cùng ý nghĩa để tặng bạn bè, người thân nhân dịp đặc biệt.</p>\r\n<p>Đèn sứ thấu quang GLAM bát giác hoa văn nổi có hoạ tiết hoa văn cổ điển mang đến không gian nhà bạn thêm sang trọng, nổi bật.</p>', 5, 'A', NULL, NULL, '2017-04-23 12:16:20', 1, NULL, NULL);
INSERT INTO `product` VALUES (223, NULL, 'Đèn sứ thấu quang GLAM Mộng phi điệp', 'DSTQ-0005', 'upload/images/san-pham/den_su_thau_quang/den-su.jpg', 'normal', NULL, 2900000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p><strong>Đèn sứ thấu quang GLAM Mộng Phi Điệp</strong> là một tác phẩm nghệ thuật độc đáo, được làm từ sương đất cao cấp, nung ở nhiệt độ gần 1300 độ C đảm bảo độ sáng trong, bền đẹp của sản phẩm. </p>\r\n<p>Với tinh hoa gốm sứ lâu đời, người thợ gốm đã thổi hồn vào chiếc đèn khắc lên từng hoạ tiết tỉ mỉ, tinh tế. Thiết kế dáng dế, hoạ tiết  bướm 3D sống động, sắc nét, đa dạng hoa văn và màu sắc. Sản phẩm  mang đến không gian sang trọng, mới mẻ cho ngôi nhà của bạn.</p>\r\n<p><strong>Đèn sứ thấu quang GLAM Mộng Phi Điệp</strong> có thể trưng bày, làm đèn trang trí, đèn ngủ, khi bật điện lên ánh sáng xuyên qua lớp sương sứ tạo cho hình ảnh có chiều sâu, sống động đa chiều, họa tiết tinh tế lung linh huyền ảo. Chân đế đã được đấu sẵn dây điện nguồn điện 220v với công xuất bóng điện bên trong 40w, có công tắc bật tắt khi sử dụng. Chân đế rời , dễ dàng thay bóng đèn và vệ sinh đèn khi sử dụng. </p>\r\n<p>Sản phẩm cũng là món quà vô cùng ý nghĩa để tặng bạn bè, người thân nhân dịp đặc biệt. </p>\r\n<p> </p>\r\n<p>Bướm là hiện thân của sự chuyển đổi, từ một con nhộng xấu xí trở thành con vật màu sắc sặc sỡ, lộng lẫy nên có thể cải thiện cuộc sống của bạn. Bướm gắn liền với tích truyện đầy nước mắt Lương Sơn Bá, Chúc anh Đài  một chàng thư sinh nghèo yêu tiểu thư một gia đình quan lại giàu có. Hai người yêu nhau nhưng bị cha mẹ của cô gái ngăn cấm. Họ hết sức đau khổ và tìm đến cái chết. Sau khi chết, cả hai hóa thành bướm và mãi mãi bên nhau.  Chính vì vậy, loài vật này còn là biểu tượng của tình yêu và lãng mạn, sự gắn bó bất diệt giữa những đôi tình nhân khát khao về cuộc sống tự do, tự tại. </p>', 6, 'A', NULL, NULL, '2017-04-23 12:18:28', 1, NULL, NULL);
