INSERT INTO `product` VALUES (324, NULL, 'Bộ khay đun cà phê vuốt tay', 'CF-DUN04', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 100000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bộ bếp đun cà phê vuốt tay được làm từ chất liệu gốm sứ cao cấp, nung ở nhiệt độ gần 1300 độ C loại bỏ được hết tạp chất gây hại cho sức khoẻ. Thiết kế độc đáo sang trọng giúp bạn thêm cảm hứng khi tự tay chế biến và thưởng thức cafe ngay tại nhà. </p>\r\n<p>Sản phẩm thích hợp để làm quà tặng bạn bè, người thân nhân các dịp đặc biệt.</p>', 6, 'A', NULL, NULL, '2017-04-29 13:02:01', 1, '2017-04-29 13:02:27', 1);
INSERT INTO `product` VALUES (325, NULL, 'Phin cà phê vuốt tay', 'CF-PHIN', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 150000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p><strong>Phin cà phê vuốt tay</strong> là sản phẩm gốm sứ Bát Tràng cao cấp. Phin cà phê vuốt tay được nung ở nhiệt độ gần 1300 độ C, cốt sứ bền, đẹp, đặc biệt loại bỏ được hết tạp chất độc hại, đảm bảo sức khoẻ người dùng.</p>\r\n<p>Thiết kế độc đáo, sang trọng thêm cảm hứng thưởng thức hương vị cà phê của bạn.</p>\r\n<p>Sản phẩm thích hợp làm quà tặng bạn bè, người thâm nhân dịp lễ, tết, sinh nhật,.....</p>', 3, 'A', NULL, NULL, '2017-04-29 13:03:30', 1, NULL, NULL);
