INSERT INTO `product` VALUES (385, NULL, 'Tranh sứ Phố cổ số 3 (33x25)', 'TRS-DON4', 'upload/images/san-pham/tranh_gom_su/tranh-gom-su.jpg', 'normal', NULL, 200000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Tranh sứ Phố cổ là sự kết hợp hoàn hảo giữa nghệ thuật, điêu khắc, đắp nổi, kỹ thuât gốm công nghệ cao tạo nên sản phẩm chất lượng đỉnh cao. Sản phẩm được nung ở nhiệt độ gần 1300 độ C nên nước men sáng bóng bền màu vĩnh cửu với thời gian. Hoạ tiết Phố Cổ được vẽ bởi đôi tay tài hoa của người nghệ nhân làng gốm, bút pháp tinh tế, có hồn.</p>\r\n<p>Tranh sứ Phố Cổ thích hợp dùng để trưng bày ở phòng khách, phòng làm việc hoặc có thể làm quà tặng bạn bè người thân nhân dịp đặc biệt.</p>\r\n<p>Kích thước: 33 cm x 25 cm</p>', 5, 'A', NULL, NULL, '2017-05-14 15:39:34', 1, NULL, NULL);
INSERT INTO `product` VALUES (386, NULL, 'Tranh sứ Phố cổ số 3 mẫu 2 (33x25)', 'TRS-DON4', 'upload/images/san-pham/tranh_gom_su/tranh-gom-su.jpg', 'normal', NULL, 200000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<div id=\"description\" class=\"tab-pane active\">\r\n<p style=\"text-align: justify;\">Tranh sứ Phố cổ là sự kết hợp hoàn hảo giữa nghệ thuật, điêu khắc, đắp nổi, kỹ thuât gốm công nghệ cao tạo nên sản phẩm chất lượng đỉnh cao. Sản phẩm được nung ở nhiệt độ gần 1300 độ C nên nước men sáng bóng bền màu vĩnh cửu với thời gian. Hoạ tiết Phố Cổ được vẽ bởi đôi tay tài hoa của người nghệ nhân làng gốm, bút pháp tinh tế, có hồn.</p>\r\n<p style=\"text-align: justify;\"><strong> Tranh sứ Phố</strong> <strong>cổ</strong> tượng trưng co 36 phố phường, treo bức tranh sứ phố cổ vừa mang nét văn hóa dân tộc vừa mang tài lộc về nhà.</p>\r\n<p style=\"text-align: justify;\">Tranh sứ Phố Cổ thích hợp dùng để trưng bày ở phòng khách, phòng làm việc hoặc có thể làm quà tặng bạn bè người thân nhân dịp đặc biệt.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">Phố cổ Hà Nội nằm ở ngoại hoàng thành Thăng Long, nơi đây tập trung dân cư hoạt động tiểu thủ công nghiệp và buôn bán giao thương, hình thành lên những phố nghề đặc trưng, mang những nét truyền thống riêng biệt của cư dân thành thị, kinh đô. Phố cổ được biết đến như linh hồn của Hà Nội, con phố hàng nghìn năm tuổi với những ngôi nhà cổ kính,rêu phong. Phố cổ hiện hữu với những hình ảnh rất đỗi quen thuộc, thân thương. Hình ảnh phố cổ mang đậm phong vị xưa, những con phố trông vắng vẻ, trầm mặc, người qua người lại, từ dân nghèo gồng gánh đến quan đại thần ngồi kiệu che lọng. Phụ nữ vấn tóc đuôi gà, mặc áo tứ thân, đi dép cong, đội nón quai thao… Nam giới búi tóc củ hành, mặc áo the thâm, xe kéo…hồn cốt của Phố Cổ những thập niên 50, 60,70 vẫn vấn vương đâu đây gợi nét nhớ trong lòng người con Hà Nội.</p>\r\n</div>', 6, 'A', NULL, NULL, '2017-05-14 15:42:52', 1, '2017-05-14 15:43:36', 1);
