INSERT INTO `product` VALUES (462, NULL, 'Lọ Nâu Cao S2', 'LH-XK-0040', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 180000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p><strong>Lọ hoa nâu cao</strong> là một dòng sản phẩm của gốm sứ Bát Tràng. Với thiết kế thân bình cao, miệng thắt cổ tròn kết hợp với tráng men màu nâu trầm - đơn giản, nhẹ nhàng nhưng vẫn toát nên được nét sang trọng, ấm cúng cho không gian nhà bạn.</p>\r\n<p>Sản phẩm thích hợp dùng để cắm hoa, trưng bày nghệ thuật, làm quà tặng cho bạn bè, người thân nhân dịp lễ, sinh nhật, tân gia,...</p>', 8, 'A', NULL, NULL, '2017-05-15 14:32:20', 1, '2017-05-15 19:03:30', 1);
