INSERT INTO `product` VALUES (464, NULL, 'Lọ Tảng Đá Đen S2', 'LH-XK-0106', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 140000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<table width=\"740\">\r\n<tbody>\r\n<tr>\r\n<td width=\"426\">\r\n<p>Lọ tảng đá thuộc dòng hàng xuất khẩu của gốm sứ Bát Tràng. Sản phẩm được thiết kế góc cạnh, mới mẻ mang hơi thở của thời đại mới. Lọ tảng đá đen dùng để cắm hoa hoặc trưng bày ở phòng làm việc, trang trí nhà cửa góp phần làm cho không gian sống của bạn thêm bắt mắt và thẩm mỹ, tạo thêm cảm hứng sống và làm việc cho chính bạn.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 9, 'A', NULL, NULL, '2017-05-15 14:34:35', 1, '2017-05-15 19:12:48', 1);
INSERT INTO `product` VALUES (465, NULL, 'Lọ Tảng Đá Xanh Ngọc S2', 'LH-XK-0108', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 140000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ tảng đá xanh ngọc thuộc dòng hàng xuất khẩu của gốm sứ Bát Tràng. Sản phẩm có thiết kế góc cạnh, kết  hợp với màu xanh ngọc thanh nhã là điểm nhấn đặc biệt của lọ hoa. Sản phẩm dùng để cắm hoa hoặc trưng bày ở phòng làm việc, trang trí nhà cửa góp phần làm cho không gian sống của bạn thêm bắt mắt và thẩm mỹ, thêm cảm hứng sống và làm việc cho chính bạn.</p>\r\n<p>Sản phẩm cũng là lựa chọn thông minh làm quà tặng cho bạn bè, người thân nhân dịp lễ tết, tân gia....</p>', 5, 'A', NULL, NULL, '2017-05-15 14:35:37', 1, '2017-05-15 19:13:41', 1);
