INSERT INTO `product` VALUES (475, NULL, 'Chum Trắng Men Co', 'LH-XK-0021', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 120000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Chum trắng men co - Dòng lọ hoa Bát Tràng cao cấp, được vẽ nặn tinh tế bởi đôi bàn tay khéo léo của người thợ gốm. Lọ có dáng chum, sử dụng chất men co mang lại vể đẹp trang nhã cho sản phẩm. Với màu kem nhẹ nhàng làm tôn nên vẻ đẹp của hoa khi được cắm lọ. </p>\r\n<p>Sản phẩm thích hợp để cắm hoa bày phòng ngủ, phòng khách hay dùng để trưng bày trang trí, làm quà tặng cho bạn bè, người thân nhân dịp lễ, tết, sinh nhật, tân gia,....</p>', 6, 'A', NULL, NULL, '2017-05-15 15:25:33', 1, '2017-05-15 19:18:50', 1);
INSERT INTO `product` VALUES (476, NULL, 'Bình Tròn Miệng Loe S2 Màu Ghi', 'LH-XK-0081', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 140000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bình tròn miệng loe S2 màu ghi có thiết kế  thân bình tròn miệng loe vì vậy, khi cắm hoa, các cành hoa sẽ loe theo miệng bình tán rộng tạo được chiều sâu cho không gian của bạn. </p>\r\n<p>Sản phẩm thích hợp dùng để cắm hoa hoặc bày bài trí ở phòng khách, phòng làm việc, làm quà tặng bạn bè người thân nhân dịp lễ, sinh nhật, tân gia....</p>', 6, 'A', NULL, NULL, '2017-05-15 15:26:25', 1, '2017-05-15 19:20:04', 1);
INSERT INTO `product` VALUES (477, NULL, 'Vò Chân Cao Vẽ Sơn Thuỷ', 'LH-XK-0091', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 200000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Vò chân cao vẽ sơn thuỷ thuộc dòng lọ hoa cao cấp của gốm sứ Bát Tràng. Sản phẩm được làm từ đất sét cao lanh, nung ở nhiệt độ gần 1300 độ C. Với thiết kế theo phong cách cổ điển, hoạ tiết vẽ tay sơn thuỷ bay bổng tạo nên sự tinh tế, nghệ thuật cho sản phẩm.</p>\r\n<p>Sản phẩm thích hợp cắm các dáng hoa thân cao như: ly, hướng dương, cúc, lau, sen,....Đặc biệt, sản phẩm có tính nghệ thuật cao nên có thể trưng bày nội thất, làm quà tặng bạn bè, người thân.....</p>', 6, 'A', NULL, NULL, '2017-05-15 15:27:31', 1, '2017-05-15 19:20:36', 1);
INSERT INTO `product` VALUES (478, NULL, 'Vò Con Nghê', 'LH-XK-0089', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 250000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Vò con Nghê được làm từ chất liệu đất cao cấp của Bát Tràng, hoạ tiết con nghê truyền thống vẽ trên nền trằng làm tôn nên nét cổ điển, tinh tế cuả sản phẩm. Sản phẩm thích hợp để cắm các loại hoa thân cao như hướng dương, sen, cúc, lau,...trưng bày, làm quà tặng cho bạn bè người thân nhân dịp lễ tết. </p>', 6, 'A', NULL, NULL, '2017-05-15 15:28:29', 1, '2017-05-15 19:22:24', 1);
INSERT INTO `product` VALUES (479, NULL, 'Lọ Miệng Vát S2 Đen', 'LH-XK-0115', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 90000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ miệng vát S2 đen là sản phẩm đẹp và lạ lẫm một sản phẩm của gốm sứ Bát Tràng. Sản phẩm được thiết kế đơn giản, theo phong cách hiện đại thích hợp với cắm hoa khô, hoa nghệ thuật và để trang trí. Một món quà ý nghĩa cho người thân của bạn .</p>', 4, 'A', NULL, NULL, '2017-05-15 15:29:16', 1, '2017-05-15 19:22:45', 1);
