INSERT INTO `product` VALUES (486, NULL, 'Bình Sữa Trắng Vàng Lùn', 'LH-XK-0125', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 70000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bình sữa trắng vàng lùn nằm trong bộ sưu tập lọ hoa dáng mới của gốm sứ Bát Tràng. Sản phẩm có thiết kế nhỏ xinh, đơn giản theo dáng bình sữa tạo cảm giác gần gũi và độc đáo cho người nhìn.</p>\r\n<p>Bình sữa trắng vàng lùn thích hợp cắm các loại hoa giấy, hoa nghệ thuật hoặc để trưng bày ở bàn làm việc, góc học tập. Đặc biệt, sản phẩm là món quà ý nghĩa để tặng bạn bè, người thân.</p>', 7, 'A', NULL, NULL, '2017-05-15 15:37:30', 1, '2017-05-15 19:35:16', 1);
INSERT INTO `product` VALUES (487, NULL, 'Trụ Tròn Vai Vuông Cổ Đứng S3 Màu Ghi', 'LH-XK-0132', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 220000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Trụ tròn vai vuông cổ đứng S3 màu ghi thuộc dòng lọ hoa nghệ thuật của gốm sứ Bát Tràng. Với thiết kế độc đáo có hình trụ tròn kết hợp với cổ đứng và được nung ở nhiệt độ gần 1300 độ C nên sản phẩm bền, đẹp và có tính nghệ thuật cao. Sản phẩm thích hợp để trưng bày trang trí trong phòng khách, nhà bếp giúp không gian sống thêm hiện đại, ấn tượng. Sản phẩm cũng là món quà ý nghĩa để tặng bạn bè, người thân nhân dịp lễ tết, sinh nhật, tân gia,...</p>', 4, 'A', NULL, NULL, '2017-05-15 15:38:16', 1, '2017-05-15 19:42:51', 1);
INSERT INTO `product` VALUES (488, NULL, 'Lọ Ống Méo Trắng Bóng', 'LH-XK-0059', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 200000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ ống méo trắng bóng là một dòng sản phẩm xuất khẩu của gốm sứ Bát Tràng. Với thiết kế hiện đại, cốt sứ nhẹ, bao phủ bởi lớp màu trắng bóng giúp sản phẩm càng trở nên tinh tế, thanh thoát.</p>\r\n<p>Sản phẩm thích hợp để cắm hoa, trưng bày nội thất và làm quà tặng bạn bè, người thân. </p>', 6, 'A', NULL, NULL, '2017-05-15 15:39:11', 1, '2017-05-15 19:36:18', 1);
INSERT INTO `product` VALUES (489, NULL, 'Lọ Ống Méo Trắng Trơn S2', 'LH-XK-0058', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 140000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ ống méo trắng trơn S2 là một dòng sản phẩm xuất khẩu của gốm sứ Bát Tràng. Với thiết kế hiện đại, cốt sứ nhẹ, bao phủ bởi lớp màu trắng đơn giản giúp sản phẩm càng trở nên tinh tế, thanh thoát.</p>\r\n<p>Sản phẩm thích hợp để cắm hoa, trưng bày nội thất và làm quà tặng bạn bè, người thân.</p>', 5, 'A', NULL, NULL, '2017-05-15 15:40:06', 1, '2017-05-15 19:42:32', 1);
INSERT INTO `product` VALUES (490, NULL, 'Lọ ống 2 màu S2', 'LH-XK-0068', 'upload/images/san-pham/lo_hoa/lo-hoa.jpg', 'normal', NULL, 160000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ ống 2 màu S2 thuộc dòng lọ hoa xuất khẩu của gốm sứ Bát Tràng. Thiết kế hình ống độc đáo với gam màu trắng xanh tạo nên sự thanh thoát, nhẹ nhàng của sản phẩm. </p>\r\n<p>Bên cạnh việc sử dụng để cắm hoa, sản phẩm thích hợp để trưng bày nội thất, làm quà tặng bạn bè, người thân.</p>', 6, 'A', NULL, NULL, '2017-05-15 15:41:00', 1, '2017-05-15 19:43:36', 1);
