INSERT INTO `product` VALUES (510, NULL, 'Tỏi sen vàng', 'LH-XK-0026', 'upload/images/deco/20961147_10203617240468642_1424917216_o.jpg', 'normal', NULL, 0.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lu kết tinh S2 là một dòng của gốm sứ Bát Tràng. Thiết kế đơn giản với men kết tinh màu hồng độc đáo, tạo nên sản phẩm ấn tượng và đẹp mắt. Sự kết tinh của men và đất nung ở nhiệt độ 1300 độ C tạo ra kết tủa dưới dàng bông hoa tròn hoàn toàn tư nhiên, màu sắc lấp lánh như khảm trai tinh tế và không có sự trùng lặp.</p>\r\n<p>Sản phẩm thích hợp để cắm hoa, trang trí nội thất, làm quà tặng bạn bè, người thân</p>', 6, 'A', NULL, NULL, '2017-05-15 20:08:31', 1, '2017-08-19 17:37:35', 1);
INSERT INTO `product` VALUES (511, NULL, 'Tỏi sen đêm', 'LH-XK-0022', 'upload/images/deco/20967949_10203617243508718_970944862_o.jpg', 'normal', NULL, 220000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ hoa xanh dáng mới S1 được làm từ đất sét cao lanh Bát Tràng, thân lọ hình ống, miệng tròn kết hợp với màu men xanh nhẹ nhàng, thanh thoát làm tôn lên nét tự nhiên, tinh tế của sản phẩm. Lọ hoa xanh dáng mới là một tác phẩm nghệ thuật. Bên cạnh để cắm hoa, lọ còn được dùng để trang trí, làm quà tặng cho bạn bè, người thân.</p>', 5, 'A', NULL, NULL, '2017-05-15 20:12:37', 1, '2017-08-19 17:36:56', 1);
INSERT INTO `product` VALUES (512, NULL, 'Đùi Dế bọt', 'LH-XK-0023', 'upload/images/deco/20952052_10203617244508743_2017210368_o.jpg', 'normal', NULL, 70000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ hoa xanh dáng mới S3 được làm từ đất sét cao lanh Bát Tràng, thân lọ hình ống, miệng tròn kết hợp với màu men xanh nhẹ nhàng, thanh thoát làm tôn lên nét tự nhiên, tinh tế của sản phẩm. Lọ hoa xanh dáng mới là một tác phẩm nghệ thuật. Bên cạnh để cắm hoa, lọ còn được dùng để trang trí, làm quà tặng cho bạn bè, người thân.</p>', 6, 'A', NULL, NULL, '2017-05-15 20:14:30', 1, '2017-08-19 17:35:49', 1);
INSERT INTO `product` VALUES (513, NULL, 'Khía miệng đỏ', 'LH-XK-0015', 'upload/images/deco/20967744_10203617243228711_1027144861_o.jpg', 'normal', NULL, 450000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<table width=\"740\">\r\n<tbody>\r\n<tr>\r\n<td width=\"426\">\r\n<p>Bình cam miệng loe vẽ sen được làm từ chất liệu gốm sứ cao cấp, là sản phẩm sen vẽ tay độc quyền. Hoạ tiết sen mềm mại, tinh tế kết hợp với gam màu cam vui vẻ làm tôn nên sự thanh thoát và sang trọng của sản phẩm. </p>\r\n<p>Binh cam miệng loe vẽ sen thích hợp dùng để cắm hoa, trưng bày trang trí, làm quà tặng bạn bè, người thân.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 8, 'A', NULL, NULL, '2017-05-15 20:16:16', 1, '2017-08-19 17:35:03', 1);
INSERT INTO `product` VALUES (514, NULL, 'Tì bà ', 'LH-XK-0013', 'upload/images/deco/20951996_10203617245188760_1842905620_o.jpg', 'normal', NULL, 360000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bom xanh tím vẽ sen S2 là một sản phẩm nghệ thuật độc đáo. Thiết kế theo phong cách cổ điển, hoạ tiết sen vẽ tay độc quyền mềm mại, thanh thoát làm nên những nét riêng biệt của sản phẩm.  Sản phẩm thích hợp dùng để cắm hoa, trưng bày nghệ thuật và làm quà tặng cho bạn bè người thân.</p>', 8, 'A', NULL, NULL, '2017-05-15 20:17:39', 1, '2017-08-19 17:34:17', 1);
INSERT INTO `product` VALUES (515, NULL, 'Tỏi phú quý diên liên', 'LH-MR-0022', 'upload/images/deco/20960880_10203617245228761_2068436967_o.jpg', 'normal', NULL, 600000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bình đu đủ men rạn vẽ hoa xanh thuộc dòng sản phẩm gốm sứ Bát Tràng cao cấp, thiết kế đơn giản thân bầu miệng loe, tô điểm trên nền men rạn là hoạ tiết hoa văn mềm mại, tinh tế tôn nên sự sang trọng, thanh thoát cho sản phẩm.</p>\r\n<p>Bình đu đủ men rạn vẽ hoa xanh thích hợp để cắm hoa, trưng bày và làm quà tặng bạn bè người thân.</p>', 8, 'A', NULL, NULL, '2017-05-15 20:19:19', 1, '2017-08-19 17:33:20', 1);
INSERT INTO `product` VALUES (516, NULL, 'Tỏi phú quý liên hoa', 'LH-XK-0001', 'upload/images/deco/20967762_10203617245628771_1421270965_o.jpg', 'normal', NULL, 940000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Bình trứng vẽ chim hoa sứ trong thuộc dòng sản phẩm cao cấp của gốm sứ Bát Tràng. Thiết kế hình trứng đơn giản, trên nền trắng tinh tế nổi bật hoạ tiết hoa lá, chim muông sắc nét, tinh tế làm tôn lên vẻ sôi động, tươi mới của chiếc bình.</p>\r\n<p>Sản phẩm thích hợp để cắm hoa, trang trí phòng làm việc, phòng khách cũng có thể dùng làm quà biếu, quà tặng trong những dịp quan trọng như tân gia, lễ tết...<strong><br /></strong></p>', 54, 'A', NULL, NULL, '2017-05-15 20:20:34', 1, '2017-08-19 17:32:17', 1);
INSERT INTO `product` VALUES (517, NULL, 'Sợi sen vàng', 'LH-XK-0035', 'upload/images/deco/20961218_10203617240908653_139107754_o.jpg', 'normal', NULL, 600000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<p>Lọ cổ ngỗng men rạn vẽ sen lục được làm từ gốm sứ Bát Tràng cao cấp, lọ có thiết kế đơn giản, hoạ tiết sen vẽ tay mềm mại, tinh tế trên nền men rạn truyền thống. Sản phẩm thích hợp để cắm hoa, trưng bày phòng làm việc, phòng khách cũng có thể dùng làm quà biếu, quà tặng trong những dịp quan trọng như tân gia, lễ tết...</p>', 11, 'A', NULL, NULL, '2017-05-15 20:21:35', 1, '2017-08-19 17:31:16', 1);
INSERT INTO `product` VALUES (518, NULL, 'Bình sen nổi', 'LH-XK-0020', 'upload/images/deco/img_6395.jpg', 'normal', NULL, 220000.00, 0.00, 'chiếc', 0, NULL, NULL, NULL, '<table width=\"740\">\r\n<tbody>\r\n<tr>\r\n<td width=\"426\">\r\n<p>Chum 3 màu kẻ xước thuộc dòng lọ hoa nghệ thuật của gốm sứ Bát Tràng. Sản phẩm được thiết kế hình bầu dục sang trọng, tinh tế, chất liệu gốm sứ cao cấp đem đến cho sản phẩm độ bền cao.</p>\r\n<p>Sản phẩm thích hợp để cắm hoa, bày trí phòng làm việc, phòng khách, góc học tập cũng có thể dùng làm quà biếu, quà tặng trong những dịp quan trọng như tân gia, lễ tết...</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 8, 'A', NULL, NULL, '2017-05-15 20:29:32', 1, '2017-08-19 17:30:12', 1);
